mapper.select:
select 

from event_info
where id = #{id,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=INTEGER}

mapper.delete:
delete from event_info
where id = #{id,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=INTEGER}

mapper.update:
update event_info
set event_name = #{eventName,jdbcType=VARCHAR},
  level_code = #{levelCode,jdbcType=VARCHAR},
  match_mode = #{matchMode,jdbcType=INTEGER},
  week = #{week,jdbcType=INTEGER},
  season = #{season,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
  and event_id = #{eventId,jdbcType=INTEGER}
